import pic1 from './photos/1.jpeg';
import pic2 from './photos/2.jpeg';
import pic3 from './photos/3.jpeg';
import './App.css';
import { useState } from 'react';

function App() {
  const [click, setClick] = useState(1); 
  const [password, setPassword] = useState("");
  const [isPasswordValid, setIsPasswordValid] = useState(false);
  const images = [pic1, pic2, pic3];
  const [image, setImage] = useState(images[0]);
  const acceptedPassword = ""
  const clicked = (operator) => {
    if(click === 3 ){
    setClick(click + 1);
    setImage(images[2])
    }
    if (click <= 2 && operator === "n") {
      console.log(click);
    setClick(click + 1);
    setImage(images[click])
      console.log(click);
    }else if (click > 0 && operator === "b") {
      console.log(click);
    setClick(click - 1);
    setImage(images[click])
      console.log(click);
    }
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsPasswordValid(password === acceptedPassword? true : false)
  }

  return (
    <div className="App">
      <header className="App-header">
        { isPasswordValid? 
        <div
        className="images">
          <img className="image" src={image} alt={image} />
          {click <= 2? <button className="button-right" onClick={e => clicked("n")}>></button> : null}
          {click > 0? <button className="button-left" onClick={e => clicked("b")}>{"<"}</button> : null}

        </div>
        :
        <form method="post" onSubmit={handleSubmit}>
          <label>
            <p>Mot de passe</p>
            <input name="myInput" className="Input-text-original"     defaultValue={password} onChange={e => setPassword(e.target.value)}/>
          </label>
          <button type="submit">Submit form</button>
        </form>

        }
      </header>
    </div>
  );
}

export default App;
